import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import AppNavbar from './AppNavbar';

const ProductDetail
= () => {

  const initialProduct = {
    variations: []
  };

  const initialOrder = {
      status: "NEW",
      taxAmount: 0.0,
      totalAmount: 0.0,
      grandTotalAmount: 0.0,
      lineItems: []
  };

  const [product, setProduct] = useState(initialProduct);
  const [order, setOrder] = useState(initialOrder);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
      fetch(`/products/${id}`)
        .then(response => response.json())
        .then(data => {
           setProduct(data);
           let lineItems = [];
           data.variations.map(productVariation => {
             const lineItem = {
                product_variation_id: productVariation.id
             }
             lineItems.push(lineItem);
           });
           setOrder({ ...order, lineItems: lineItems });
        });
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target
    debugger;
    const index = order.lineItems.findIndex(x => x.product_variation_id === parseInt(name));
    order.lineItems[index].quantity = parseInt(value);
    setOrder({ ...order })
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

//    await fetch(`/api/product${product.id ? `/${product.id}` : ''}`, {
//      method: (product.id) ? 'PUT' : 'POST',
//      headers: {
//        'Accept': 'application/json',
//        'Content-Type': 'application/json'
//      },
//      body: JSON.stringify(product)
//    });
//    setProduct(initialFormState);
    navigate('/products');
  }

  const productVariationList = product.variations.map(productVariation => {
      return <tr key={productVariation.id}>
        <td style={{whiteSpace: 'nowrap'}}>{productVariation.name}</td>
        <td>{productVariation.description}</td>
        <td>${productVariation.price}</td>
        <td><Input type="text"
            name={productVariation.id}
            id={productVariation.id}
            value="0"
            onChange={handleChange} /></td>
      </tr>
    });

  return (<div>
      <AppNavbar/>
      <Container>
        <h2>{product.name}</h2>
        <Form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col">
                <img src={"/images/" + product.name + "/image.png"} />
            </div>
            <div className="col">
                <h3>{product.description}</h3>
                <hr/>
                <table>
                    {productVariationList}
                </table>
            </div>
          </div>
          <FormGroup>
            <h4>Sub total: {order.totalAmount}</h4>
            <h4>Tax: {order.taxAmount}</h4>
            <h4>Grand total: {order.grandTotalAmount}</h4>
            <Button color="primary" type="submit">Buy</Button>{' '}
            <Button color="secondary" tag={Link} to="/products">Cancel</Button>
          </FormGroup>
        </Form>
      </Container>
    </div>
  )
};

export default ProductDetail;
